var crypto = require('crypto');
exports.md5 = function (text) {
 	return md5(text);
};

function md5(text){
	return crypto.createHash('md5').update(text).digest('hex');	
}

// 这里是生成cookie校验码的算法
exports.generateCookieCheck =  function(uid, password, salt, uid, user_name, user_priv ) {
	return md5(uid + password + salt + uid + user_name + user_priv + uid + '咩哈哈');
};

// 这里是生成密码校验码的算法
exports.generatePasswordCheck =  function(uid, password, salt, uid, user_name, user_priv ) {
	return  md5(uid + password + md5( salt + uid + user_name + user_priv + '就不告诉你') ) ;
};
